{"filter":false,"title":"file_loader.py","tooltip":"/education_chatbot/file_loader.py","undoManager":{"mark":1,"position":1,"stack":[[{"start":{"row":0,"column":0},"end":{"row":15,"column":0},"action":"remove","lines":["from langchain.document_loaders import PyMuPDFLoader","from langchain.document_loaders import TextLoader  ","from docx import Document","","def load_docx(docx_path):","    document = Document(docx_path)","    return \"\\n\".join([para.text for para in document.paragraphs])","","def load_pdf(pdf_path):","    loader = PyMuPDFLoader(pdf_path)","    return loader.load()","","def load_text(text_path):","    loader = TextLoader(text_path)","    return loader.load()",""],"id":1},{"start":{"row":0,"column":0},"end":{"row":16,"column":0},"action":"insert","lines":["from langchain_core.documents import Document  # ← これを追加！","from langchain.document_loaders import PyMuPDFLoader, TextLoader  ","from docx import Document as DocxDocument","","def load_docx(docx_path):","    docx = DocxDocument(docx_path)","    content = \"\\n\".join([para.text for para in docx.paragraphs])","    return [Document(page_content=content, metadata={\"source\": docx_path})]  # ← 修正！","","def load_pdf(pdf_path):","    loader = PyMuPDFLoader(pdf_path)","    return loader.load()  # PyMuPDFLoader は Document を返すのでそのままでOK！","","def load_text(text_path):","    loader = TextLoader(text_path)","    return loader.load()  # TextLoader も Document を返すのでOK！",""]}],[{"start":{"row":0,"column":46},"end":{"row":0,"column":57},"action":"remove","lines":[" # ← これを追加！"],"id":2}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":0,"column":0},"end":{"row":16,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1744002591008,"hash":"40f74970153d4544e7e5dc4fc34c6f6510db1fb8"}